import React from 'react'

export default class GamePlayHeader extends React.Component {
  state = {
    score: 0
  }

  render() {
    return (
      <div>
        <h1>Lutrivia</h1>
        <button onClick={this.newGame}>New Game</button>
        <p>Score: {this.state.score}</p>
      </div>
    )
  }
}
