import React from 'react'
import '../App.css'

export default class Question extends React.Component {

  handleAnswer = (e) => {
    let scoreValue;
    if (!!e.target.name === this.props.question.answer) {
      this.buttonColor = "green"
      scoreValue = 1
    } else {
      this.buttonColor = "red"
      scoreValue = 0
    }
    this.props.updateScore(scoreValue)
  }

  render() {
    console.log("re-rendered")
    return (

      <div className="question-card">
        {this.props.question.text}
        <div>
          <button className={`btn-${this.buttonColor}`} name="true" onClick={this.handleAnswer}>True</button>
          <button className={`btn-${this.buttonColor}`} name="false" onClick={this.handleAnswer}>False</button>
        </div>
      </div>
    )
  }

}
