import React from 'react'
import '../App.css'

export default class Question extends React.Component {

  state = {
    isCorrect: "NA"
  }

  handleAnswer = (e) => {
    let scoreValue;
    if (!!e.target.name === this.props.question.answer) {
      this.setState({isCorrect: true})
      scoreValue = 1
    } else {
      this.setState({isCorrect: false})
      scoreValue = 0
    }
    this.props.updateScore(scoreValue)
  }

  applyColor = () => {
    if (this.state.isCorrect !== "NA") {
      return this.state.isCorrect ? "true" : "false"
    }
    return ""
  }

  render() {
    console.log("re-rendered")
    console.log(this.state.buttonColor)
    return (

      <div className="question-card">
        {this.props.question.text}
        <div>
          <button className={`btn ${this.applyColor()}`} name="true" onClick={this.handleAnswer}>True</button>
          <button className={`btn ${this.applyColor()}`} name="false" onClick={this.handleAnswer}>False</button>
        </div>
      </div>
    )
  }

}
