import React from 'react'
import '../App.css'

export default class Question extends React.Component {

  state = {
    buttonColor: ""
  }

  handleAnswer = (e) => {
    if (!!e.target.name === this.props.question.answer) {
      this.setState({buttonColor: "green"})
    } else {
      this.setState({buttonColor: "red"})
    }
  }

  render() {
    return (
      <div className="question-card">
        {this.props.question.text}
        <div>
          <button className={`btn-${this.state.buttonColor}`} name="true" onClick={this.colorAnswer}>True</button>
          <button name="false" onClick={this.colorAnswer}>False</button>
        </div>
      </div>
    )
  }

}
