import React from 'react'
import '../App.css'

export default class Question extends React.Component {

  state = {
    isCorrect: null
  }

  handleAnswer = (e) => {
    let scoreValue;
    if (!!e.target.name === this.props.question.answer) {
      this.setState({buttonColor: "green"})
      scoreValue = 1
    } else {
      this.setState({buttonColor: "red"})
      scoreValue = 0
    }
    this.props.updateScore(scoreValue)
  }

  render() {
    console.log("re-rendered")
    console.log(this.state.buttonColor)
    return (

      <div className="question-card">
        {this.props.question.text}
        <div>
          <button className={`btn-${this.state.buttonColor}`} name="true" onClick={this.handleAnswer}>True</button>
          <button name="false" onClick={this.handleAnswer}>False</button>
        </div>
      </div>
    )
  }

}
