    //   btnClass = "false"
    // }

    // console.log(btnClass)
    // this.setState({
    //   btnClass: btnClass
    // })
  }

  render() {
    return (
      <button 
        className={`btn ${this.state.btnClass}`}
        name={this.props.name}
        onClick={this.onClick}
        disabled={this.props.answered}
      >
        {this.props.text}
      </button>
    )
  }
}
